/// Declares all component tokens for list item components like `OUDSListItem`
/// Use for tokens providers like `OrangeThemeListItemComponentTokensProvider`.
///
/// - Since: 0.10.0
public protocol ListItemComponentTokens {

    // MARK: - Colors

    var listItemSizeMinHeight: SizeSemanticToken { get }
    var listItemSizeMinWidth: SizeSemanticToken { get }
    var listItemColorBgFocus: MultipleColorSemanticTokens { get }
    var listItemColorBgHover: MultipleColorSemanticTokens { get }
    var listItemColorBgLoading: MultipleColorSemanticTokens { get }
    var listItemColorBgPressed: MultipleColorSemanticTokens { get }
    var listItemColorContentLoader: MultipleColorSemanticTokens { get }

    // MARK: - Sizes

    var listItemSizeIcon: SizeSemanticToken { get }
    var listItemSizeLoader: SizeSemanticToken { get }

    // MARK: - Spaces

    var listItemSpaceColumnGap: SpaceSemanticToken { get }
    var listItemSpaceInset: SpaceSemanticToken { get }
    var listItemSpaceRowGap: SpaceSemanticToken { get }
}

